mapper.select:
select count(1)
from orders


        and id = #{id}


        and uid = #{uid}


        and total_money = #{totalMoney}


        and pay_type = #{payType}


        and flag = #{flag}


        and create_time = #{createTime}


        and update_time = #{updateTime}


        and `no` = #{no}


        and project_id = #{projectId}


        and project_name = #{projectName}


        and physio_id = #{physioId}


        and physio_name = #{physioName}
